css:
.details {
    font-size: 15px;
    font-family: Tahoma, sans-serif;;
    line-height: 150%;
}

.certificate-footer {
    font-size: 15px;
    font-family: Tahoma, sans-serif;
    line-height: 140%;
    margin-top: 120px;
}

.company-address {
    color: #666666;
    font-size: 15px;
    font-family: Tahoma, sans-serif;;
}

html:
{% if letter_head and not no_letterhead -%}
    <div class="letter-head">{{ letter_head }}</div>
{%- endif %}

<div>
    <h3 class="text-center">{{ doc.company }} 80G Donor Certificate</h3>
</div>
<br><br>

<div class="details">
    <p> <b>{{ _("Certificate No. : ") }}</b> {{ doc.name }} </p>
    <p>
    	<b>{{ _("Date") }} :</b> {{ doc.get_formatted("date") }}<br>
    </p>
    <br><br>

    <div>

        This is to confirm that the {{ doc.company }} received an amount of <b>{{doc.get_formatted("amount")}}</b>
        from <b>{{ doc.donor_name }}</b>
        {% if doc.pan_number -%}
            bearing PAN Number {{ doc.member_pan_number }}
        {%- endif %}

        via the Mode of Payment {{doc.mode_of_payment}}

        {% if doc.payment_id -%}
            bearing Payment ID {{ doc.payment_id }}
        {%- endif %}

        on {{ doc.get_formatted("date_of_donation") }}
        <br><br>

        <p>
            We thank you for your contribution towards the corpus of the {{ doc.company }} and helping support our work.
        </p>

    </div>
</div>

<br><br>
<p class="company-address text-left"> {{doc.company_address_display }}</p>

<div class="certificate-footer text-center">
    <p><i>Computer generated receipt - Does not require signature</i></p><br>

    {% if doc.company_pan_number %}
    <p>
        <b>{{ doc.company }}'s PAN Account No :</b> {{ doc.company_pan_number }}
    <p><br>
    {% endif %}

    <p>
        <b>80G Number : </b> {{ doc.company_80g_number }}
        {% if doc.company_80g_wef %}
            ( w.e.f. {{ doc.get_formatted('company_80g_wef') }} )
        {% endif %}
    </p><br>
</div>

css:
.qr-code{
    float:right;
}

.invoice-heading {
  margin: 0;
}

.ksa-invoice-table {
  border: 1px solid #888a8e;
  border-collapse: collapse;
  width: 100%;
  margin: 20px 0;
  font-size: 16px;
}

.ksa-invoice-table.two-columns td:nth-child(2) {
  direction: rtl;
}

.ksa-invoice-table th {
  border: 1px solid #888a8e;
  max-width: 50%;
  padding: 8px;
}

.ksa-invoice-table td {
  padding: 5px;
  border: 1px solid #888a8e;
  max-width: 50%;
}

.ksa-invoice-table thead,
.ksa-invoice-table tfoot {
  text-transform: uppercase;
}

.qr-rtl {
  direction: rtl;
}

.qr-flex{
    display: flex;
    justify-content: space-between;
}

.print-format th {
    background-color: transparent !important;
    border-bottom: 1px solid !important;
    border-top: none !important;
}
.print-format .ql-editor {
    padding-left: 0px;
    padding-right: 0px;
}

.print-format table {
    margin-bottom: 0px;
    }
.print-format .table-data tr:last-child { 
    border-bottom: 1px solid !important;
}

.print-format .table-inner tr:last-child {
    border-bottom:none !important;
}
.print-format .table-inner {
    margin: 0px 0px;
}

.print-format .table-data ul li { 
    color:#787878 !important;
}

.no-top-border {
    border-top:none !important;
}

.table-inner td {
    padding-left: 0px !important;    
    padding-top: 1px !important;
    padding-bottom: 1px !important;
    color:#787878 !important;
}

.total {
    background-color: lightgrey !important;
    padding-top: 4px !important;
    padding-bottom: 4px !important;
}

.details {
    font-size: 15px;
    font-family: Tahoma, sans-serif;;
    line-height: 150%;
}

.certificate-footer {
    font-size: 15px;
    font-family: Tahoma, sans-serif;
    line-height: 140%;
    margin-top: 120px;
}

.company-address {
    color: #666666;
    font-size: 15px;
    font-family: Tahoma, sans-serif;;
}

